import { Button } from "@/components/ui/button";
import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import {
  InputOTP,
  InputOTPGroup,
  InputOTPSeparator,
  InputOTPSlot,
} from "@/components/ui/input-otp";

export const title = "Two-Factor Authentication";

const formSchema = z.object({
  code: z.string().min(6, {
    message: "Authentication code must be 6 characters.",
  }),
  backupCode: z.string().optional(),
});

const Example = () => {
  const [useBackupCode, setUseBackupCode] = useState(false);

  const form = useForm<z.infer<typeof formSchema>>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      code: "",
      backupCode: "",
    },
  });

  function onSubmit(values: z.infer<typeof formSchema>) {
    if (useBackupCode) {
      toast.success(`Authenticated with backup code: ${values.backupCode}`);
    } else {
      toast.success(`Authenticated with code: ${values.code}`);
    }
  }

  return (
    <div className="w-full max-w-sm space-y-6">
      <div className="flex flex-col items-center space-y-2 text-center">
        <div className="flex h-12 w-12 items-center justify-center rounded-full bg-primary/10">
