              {features.map((feature) => (
                <CommandItem
                  className="flex items-start gap-3 py-3"
                  key={feature.value}
                  onSelect={(currentValue) => {
                    setValue(currentValue === value ? "" : currentValue);
                    setOpen(false);
                  }}
                  value={feature.value}
                >
                  <Check
                    className={cn(
                      "mt-0.5 size-4 shrink-0",
